using Components;using System;using System.Runtime.CompilerServices;namespace HECSFramework.Core{	public partial class ComponentContext : IDisposable	{		public ActorContainerID GetActorContainerID { get; private set; }		public ConnectionsHolderComponent GetConnectionsHolderComponent { get; private set; }		public LocationComponent GetLocationComponent { get; private set; }		public ClientIDHolderComponent GetClientIDHolderComponent { get; private set; }		public NetworkComponentsHolder GetNetworkComponentsHolder { get; private set; }		public ServerTagComponent GetServerTagComponent { get; private set; }		public SyncEntitiesHolderComponent GetSyncEntitiesHolderComponent { get; private set; }		public WorldSliceIndexComponent GetWorldSliceIndexComponent { get; private set; }		partial void Add(IComponent component)		{			switch (component)			{				case ActorContainerID actorContainerID:					{						GetActorContainerID = actorContainerID;						return;					}				case ConnectionsHolderComponent connectionsHolderComponent:					{						GetConnectionsHolderComponent = connectionsHolderComponent;						return;					}				case LocationComponent locationComponent:					{						GetLocationComponent = locationComponent;						return;					}				case ClientIDHolderComponent clientIDHolderComponent:					{						GetClientIDHolderComponent = clientIDHolderComponent;						return;					}				case NetworkComponentsHolder networkComponentsHolder:					{						GetNetworkComponentsHolder = networkComponentsHolder;						return;					}				case ServerTagComponent serverTagComponent:					{						GetServerTagComponent = serverTagComponent;						return;					}				case SyncEntitiesHolderComponent syncEntitiesHolderComponent:					{						GetSyncEntitiesHolderComponent = syncEntitiesHolderComponent;						return;					}				case WorldSliceIndexComponent worldSliceIndexComponent:					{						GetWorldSliceIndexComponent = worldSliceIndexComponent;						return;					}			}		}		partial void Remove(IComponent component)		{			switch (component)			{				case ActorContainerID actorContainerID:					{						GetActorContainerID = null;						return;					}				case ConnectionsHolderComponent connectionsHolderComponent:					{						GetConnectionsHolderComponent = null;						return;					}				case LocationComponent locationComponent:					{						GetLocationComponent = null;						return;					}				case ClientIDHolderComponent clientIDHolderComponent:					{						GetClientIDHolderComponent = null;						return;					}				case NetworkComponentsHolder networkComponentsHolder:					{						GetNetworkComponentsHolder = null;						return;					}				case ServerTagComponent serverTagComponent:					{						GetServerTagComponent = null;						return;					}				case SyncEntitiesHolderComponent syncEntitiesHolderComponent:					{						GetSyncEntitiesHolderComponent = null;						return;					}				case WorldSliceIndexComponent worldSliceIndexComponent:					{						GetWorldSliceIndexComponent = null;						return;					}			}		}		public void Dispose()		{			GetActorContainerID = null;			GetConnectionsHolderComponent = null;			GetLocationComponent = null;			GetClientIDHolderComponent = null;			GetNetworkComponentsHolder = null;			GetServerTagComponent = null;			GetSyncEntitiesHolderComponent = null;			GetWorldSliceIndexComponent = null;		}	}}namespace HECSFramework.Core{	public static class EntityComponentExtentions	{		[MethodImpl(MethodImplOptions.AggressiveInlining)]		public static ActorContainerID GetActorContainerID(this IEntity entity)		{			return entity.ComponentContext.GetActorContainerID;		}		[MethodImpl(MethodImplOptions.AggressiveInlining)]		public static ConnectionsHolderComponent GetConnectionsHolderComponent(this IEntity entity)		{			return entity.ComponentContext.GetConnectionsHolderComponent;		}		[MethodImpl(MethodImplOptions.AggressiveInlining)]		public static LocationComponent GetLocationComponent(this IEntity entity)		{			return entity.ComponentContext.GetLocationComponent;		}		[MethodImpl(MethodImplOptions.AggressiveInlining)]		public static ClientIDHolderComponent GetClientIDHolderComponent(this IEntity entity)		{			return entity.ComponentContext.GetClientIDHolderComponent;		}		[MethodImpl(MethodImplOptions.AggressiveInlining)]		public static NetworkComponentsHolder GetNetworkComponentsHolder(this IEntity entity)		{			return entity.ComponentContext.GetNetworkComponentsHolder;		}		[MethodImpl(MethodImplOptions.AggressiveInlining)]		public static ServerTagComponent GetServerTagComponent(this IEntity entity)		{			return entity.ComponentContext.GetServerTagComponent;		}		[MethodImpl(MethodImplOptions.AggressiveInlining)]		public static SyncEntitiesHolderComponent GetSyncEntitiesHolderComponent(this IEntity entity)		{			return entity.ComponentContext.GetSyncEntitiesHolderComponent;		}		[MethodImpl(MethodImplOptions.AggressiveInlining)]		public static WorldSliceIndexComponent GetWorldSliceIndexComponent(this IEntity entity)		{			return entity.ComponentContext.GetWorldSliceIndexComponent;		}	}}